import React from 'react';
import styled from 'styled-components';
import { PieChart } from 'react-native-chart-kit';
import { BigComponentContainer } from '../_atoms/Container';
import { Info } from '../_molecules/Info';

const Container = styled(BigComponentContainer)`
	backgroundColor: #F4F4F4;
	padding: 17px;
`;

export const InfoRowContainer = styled.View`
	flexDirection: row;
	justifyContent: space-around;
	alignItems: center;
`;

const CaloriesEatenInfo = (props) => {
	return (<Info
		label="Eaten"
		value={500}
		unit="kcal"
		/>)
}

const CaloriesLeftInfo = (props) => {
	return (<Info
		label="Left"
		value={2500}
		unit="kcal"
		/>)
}

const caloriesData = [
	{
		name: "Calories Eaten",
		calories: 500,
		color: "#ffffff"
	},
	{
		name: "Calories Left",
		calories: 2000,
		color: "#000000"

	}
]

const chartConfig = {
	backgroundGradientFrom: "#1E2923",
	backgroundGradientFromOpacity: 0,
	backgroundGradientTo: "#08130D",
	backgroundGradientToOpacity: 0.5,
	color: (opacity = 1) => `rgba(26, 255, 146, ${opacity})`,
	strokeWidth: 2, // optional, default 3
	barPercentage: 0.5,
	useShadowColorFromDataset: false // optional
      };

const CaloriesPieChart = (props) => {
	return (<PieChart 
		 data={caloriesData}
		 accessor={"calories"}
		 backgroundColor={"transparent"}
		 chartConfig={chartConfig}
		/>)
}

export const CalorieTracker = (props) => {
	return (
		<Container>
			<InfoRowContainer>
				<CaloriesEatenInfo />
				<CaloriesLeftInfo />
				<CaloriesPieChart />
			</InfoRowContainer>
		</Container>
	)
}
